import React, { useState } from 'react'

const UpdatePassword = () => {

    const [isPasswordShow, setPasswordShow] = useState(false);
    const [oldPassword, setOldPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const passwordToggle = () => {
        setPasswordShow(prev => !prev)
    }

    return (
        <div>
            <h2 className='text-[26px] pl-2 mb-[25px] border-l-2 border-[#d01818]'>Social Profile Link</h2>
            <form action="">
                <div className='flex gap-6 mb-6'>
                    {/* Old password */}
                    <div className='flex-1'>
                        <label htmlFor='old-password' className='block mb-2 font-semibold text-md'>Old Password</label>
                        <div className='relative'>
                            <input
                                type={isPasswordShow ? "text" : "password"}
                                class="h-12 px-4 rounded-[4px] border border-[#e9e9e9] text-sm outline-none w-full"
                                name="password"
                                id="old-password"
                                value={oldPassword}
                                onChange={(e) => setOldPassword(e.target.value)}
                            />
                            <button type="button" className='absolute top-[50%] right-4 -translate-y-1/2' onClick={passwordToggle}>
                                {isPasswordShow ? <i className="fa-regular fa-eye"></i> : <i className="fa-regular fa-eye-slash"></i>}
                            </button>
                        </div>
                    </div>
                    {/* New password */}
                    <div className='flex-1'>
                        <label htmlFor='new-password' className='block mb-2 font-semibold text-md'>New Password</label>
                        <div className='relative'>
                            <input
                                type={isPasswordShow ? "text" : "password"}
                                class="h-12 px-4 rounded-[4px] border border-[#e9e9e9] text-sm outline-none w-full"
                                name="password"
                                id="new-password"
                                value=""
                            />
                            <button type="button" className='absolute top-[50%] right-4 -translate-y-1/2' onClick={passwordToggle}>
                                {isPasswordShow ? <i className="fa-regular fa-eye"></i> : <i className="fa-regular fa-eye-slash"></i>}
                            </button>
                        </div>
                    </div>
                    {/* Old password */}
                    <div className='flex-1'>
                        <label htmlFor='confirm-password' className='block mb-2 font-semibold text-md'>Confirm Password</label>
                        <div className='relative'>
                            <input
                                type={isPasswordShow ? "text" : "password"}
                                class="h-12 px-4 rounded-[4px] border border-[#e9e9e9] text-sm outline-none w-full"
                                name="password"
                                id="confirm-password"
                                value=""
                            />
                            <button type="button" className='absolute top-[50%] right-4 -translate-y-1/2' onClick={passwordToggle}>
                                {isPasswordShow ? <i className="fa-regular fa-eye"></i> : <i className="fa-regular fa-eye-slash"></i>}
                            </button>
                        </div>
                    </div>
                </div>
                <button type='submit' className='px-5 py-3 bg-[#d01818] text-white rounded-[4px]'>SUpdate Password</button>
            </form>
        </div>
    )
}

export default UpdatePassword
