import React from "react";

const Features = ({ features }) => {
  // categories order
  const categories = [
    "comfort",
    "entertainment",
    "exterior",
    "interior",
    "power",
    "safety",
  ];

  return (
    <div>
      <h3 className="text-2xl font-medium mb-6">Features</h3>
      <div className="space-y-6">
        {categories.map((category) =>
          features[category] && features[category].length > 0 ? (
            <div key={category}>
              <h4 className="text-base mb-4 capitalize">{category}</h4>
              <ul className="grid grid-cols-3 gap-2 text-sm font-medium text-gray-600">
                {features[category].map((item, i) => (
                  <li key={i} className="flex items-center gap-2">
                    <span>âœ…</span> {item}
                  </li>
                ))}
              </ul>
            </div>
          ) : null
        )}
      </div>
    </div>
  );
};

export default Features;
