import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

const Map = ({ location }) => {
    const [position, setPosition] = useState(null);

    useEffect(() => {
        if (!location) return;

        const fetchCoordinates = async () => {
            try {
                const cleanLocation = location
                    .replace("DE-", "")
                    .replace("and", "")
                    .replace(/\s+/g, " ")
                    .trim();

                const res = await fetch(
                    `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(cleanLocation)}`
                );
                const data = await res.json();

                if (data && data[0]) {
                    setPosition([parseFloat(data[0].lat), parseFloat(data[0].lon)]);
                } else {
                    console.error("Location not found for:", cleanLocation);
                }
            } catch (err) {
                console.error("Error fetching location:", err);
            }
        };

        fetchCoordinates();
    }, [location]);

    const markerIcon = new L.Icon({
        iconUrl: "https://cdn-icons-png.flaticon.com/512/684/684908.png",
        iconSize: [35, 35],
    });

    console.log(position);

    return (
        <div style={{ height: "400px", width: "100%" }}>
            {position ? (
                <MapContainer center={position} zoom={13} style={{ height: "100%", width: "100%", borderRadius: "10px" }}>
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors'
                    />
                    <Marker position={position} icon={markerIcon}>
                        <Popup>{location}</Popup>
                    </Marker>
                </MapContainer>
            ) : (
                <p className="text-gray-500">Loading map...</p>
            )}
        </div>
    );
};

export default Map;
