import {React,useState} from 'react'

const ReviewReply = () => {
    const [rating,setRating] =useState(false);
    const handleRating = (e) => {
        // const rating = e.target.dataset.rating;
        setRating(prev=>!prev);
    }

    return (
        <div>
            <form method="post" id="tfcl_review_form">
                <div className="mb-4">
                    <label className='text-sm font-medium mb-2 block'for="review"> Review </label>
                    <textarea id="review" name="review" placeholder="Your Message:" className='rounded-[14px] border border-[#ededed] bg-white text-sm h-[130px] w-full outline-none p-4' required></textarea>
                </div>
                <div className="flex mb-5">
                    <div className="rating-box">
                        <label className='text-sm font-medium mb-2'>Rating</label>
                        <div className="text-xs text-[#ff7101] text-[##ff7101] space-x-1 mt-1 [&>i]:cursor-pointer">
                            <i className={`${rating?'fa-solid':'fa-ragular'} fa-star`} data-rating="1" onClick={handleRating}></i>
                            <i className={`${rating?'fa-solid':'fa-ragular'} fa-star`} data-rating="2" onClick={handleRating}></i>
                            <i className={`${rating?'fa-solid':'fa-ragular'} fa-star`} data-rating="3" onClick={handleRating}></i>
                            <i className={`${rating?'fa-solid':'fa-ragular'} fa-star`} data-rating="4" onClick={handleRating}></i>
                            <i className={`${rating?'fa-solid':'fa-ragular'} fa-star`} data-rating="5" onClick={handleRating}></i>
                        </div>
                    </div>
                </div>
                <button type="submit" className="text-base font-medium bg-[#ff7101] px-4 max-w-[150px] w-full rounded-[10px] text-white h-11">Post Comment</button>
            </form>
        </div>
    )
}

export default ReviewReply
