import { React, useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const SignUp = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const [serverMessage, setServerMessage] = useState('');
    const [isPasswordShow, setPasswordShow] = useState(false);

    const navigate = useNavigate();

    const handleLoginForm = async (e) => {
        e.preventDefault();
        setEmailError('');
        setPasswordError('');
        setServerMessage('');

        try {
            const response = await axios.post('http://localhost:5000/api/auth/login', {
                email, password
            });
            setServerMessage(response.data.message || "Login successful");
            localStorage.setItem("user", JSON.stringify(response.data.user));
            navigate('/');
        }
        catch (error) {
            if (error.response && error.response.data) {
                const message = error.response.data.message;

                if (typeof message === 'string') {
                    setServerMessage(message);
                    if (message.toLowerCase().includes("email")) setEmailError(message);
                    if (message.toLowerCase().includes("password")) setPasswordError(message);
                } else {
                    setServerMessage("Something went wrong");
                }
            } else {
                setServerMessage("Server error");
            }
        }
    }

    const passwordToggle = () => {
        setPasswordShow(prev => !prev);
    }

    return (
        <div className='login-form'>
            <div>
                <h1 className="text-2xl font-semibold mb-8 text-center">Login</h1>
                {/* login form */}
                <form onSubmit={handleLoginForm}>
                    {/* Email */}
                    <div className='input-box mb-6'>
                        <label htmlFor="email" className='font-medium text-sm mb-2 hidden'>Email</label>
                        <div className='input-wrap relative'>
                            <input
                                type="text"
                                value={email}
                                placeholder='Email'
                                id='email'
                                required
                                className='w-full text-sm border-2 border-blue-950/30 rounded-[12px] pl-12 h-[52px] px-4 outline-none transition-all duration-200 ease focus:border-2 focus:border-blue-950'
                                onChange={(e) => setEmail(e.target.value)}
                            />
                            <span className='absolute top-[50%] -translate-y-1/2 left-4'><i className="fa-regular fa-envelope"></i></span>
                        </div>
                        {emailError && <p className="mt-1 text-sm text-red-500">{emailError}</p>}
                    </div>
                    {/* Password */}
                    <div>
                        <label htmlFor="password" className='font-medium text-base mb-2 hidden'>Password:</label>
                        <div className='input-wrap password-input relative'>
                            <input
                                type={isPasswordShow ? "text" : "password"}
                                value={password}
                                placeholder='Password'
                                id='password'
                                required
                                className='w-full border-2 border-blue-950/30 rounded-[12px] px-12 h-[52px] outline-none text-sm transition-all duration-200 ease focus:border-2 focus:border-blue-950'
                                onChange={(e) => setPassword(e.target.value)}
                            />
                            <span className='absolute left-4 top-[50%] -translate-y-1/2'><i className="fa-solid fa-lock"></i></span>
                            <button type="button" className='absolute top-[50%] -translate-y-1/2 right-4 text-sm' onClick={passwordToggle}>
                                {isPasswordShow ? <i className="fa-regular fa-eye text-[#303033]"></i> : <i className="fa-regular fa-eye-slash text-[#303033]"></i>}
                            </button>
                        </div>
                        {passwordError && <p className="mt-1 text-sm text-red-500">{passwordError}</p>}
                    </div>
                    {/* forget password */}
                    <div className='text-right mt-1'>
                        <Link to="/forget-password/" className='text-sm font-semibold text-blue-950/90 pb-[2px] border-b border-blue-950/90'>Forget password?</Link>
                    </div>
                    {/* Submit button */}
                    <button type='submit' className='mt-12 w-full block mx-auto h-[52px] bg-blue-950/90 rounded-[12px] text-white font-medium'>Login</button>
                    {/* Server message */}
                    {serverMessage && <p className='mt-4 text-sm text-center text-gray-700'>{serverMessage}</p>}
                </form>
                <p className='mt-2 text-gray-500 text-center text-sm'>Not account yet? <Link to='/register/' className='text-blue-950/90 font-semibold pb-[2px] border-b border-blue-950/90'>Register</Link></p>
                {/* divider */}
                <div className="mt-8 flex items-center text-sm">
                    <span className='flex-1 h-[1px] bg-[#e9e9e9]'></span>
                    <span className='mx-2'>Or</span>
                    <span className='flex-1 h-[1px] bg-[#e9e9e9]'></span>
                </div>
                {/* google facebook login */}
                <div className='flex gap-4 mt-8 text-base font-semibold text-white'>
                    <button className='flex-1 h-12 bg-black rounded-[4px] px-4 block'>
                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 48 48" width="24" height="24" className='inline-block mr-1 align-middle'>
                            <path fill="#fff" d="M23.49 12.27a11.3 11.3 0 0 1 7.95 3.11l3.54-3.54a16.1 16.1 0 0 0-11.49-4.54c-8.99 0-16.31 7.32-16.31 16.31s7.32 16.31 16.31 16.31c8.99 0 15.48-6.31 15.48-15.16 0-.98-.09-1.94-.27-2.88H23.49v5.46h8.79c-.38 2.02-1.55 3.74-3.33 4.89a10.57 10.57 0 0 1-5.46 1.44 10.86 10.86 0 0 1-10.86-10.86 10.86 10.86 0 0 1 10.86-10.86z" />
                        </svg>
                        <span className='align-middle'>Google</span>
                    </button>
                    <button className='flex-1 h-12 bg-black rounded-[4px] px-4 block'>
                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24" className='inline-block mr-2 align-middle'>
                            <circle cx="12" cy="12" r="12" fill="#ffffff" />
                            <path fill="#000000" d="M15.12 8.67h-2.17V7.08c0-.53.35-.66.6-.66h1.52V3.99L13 3.98c-2.47 0-3.03 1.84-3.03 3.02v1.67H8.89v2.73h1.08v7.37h2.98v-7.37h2l.31-2.73z" />
                        </svg>
                        <span className='align-middle'>Facebook</span>
                    </button>
                </div>
            </div>
        </div>
    )
}

export default SignUp
