import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'

const Header = () => {

  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);


  return (
    <header className='sticky top-0 w-full py-6 shadow-lg z-[1000]'>
      <div class="container max-w-[1200px] w-full mx-auto px-2 flex items-center justify-between">
        {/* Logo */}
        <a href="" className='text-3xl font-bold'>Logo</a>
        {/* Right header */}
        <div className='flex items-center gap-8'>
          {/* Search button */}
          <button><i class="fa-solid fa-magnifying-glass"></i></button>
          {/* Favorite button */}
          <button><i class="fa-regular fa-heart"></i></button>
          {
            !user ? (
              <div className='py-2 px-4 rounded-full bg-white shadow-[0px_4px_24px_0px_rgba(0,0,0,0.08)] text-sm flex gap-1 items-center'>
                <Link to='/register/'>Register</Link>
                <span>/</span>
                <Link to='/login/'>Login</Link>
              </div>
            )
              : (
                <Link className='w-10 h-10 bg-white rounded-full text-[#ff7101] font-bold text-xl flex justify-center items-center shadow-[0px_4px_24px_0px_rgba(0,0,0,0.08)]'>
                  <span>A</span>
                  {/* <img src="" alt=""/> */}
                </Link>
              )
          }
          {/* Add car button */}
          <button className='px-4 py-2 rounded-full bg-[#ff7101] text-white text-sm'>
            <i class="fa-solid fa-car"></i>
            <span className='inline-block ml-2'>Sell your car</span>
          </button>
        </div>
      </div>
    </header>
  )
}

export default Header
