import React, { useState } from 'react'
import axios from 'axios';
const LoginForm = () => {

  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');



  const handleRegisterForm = async () => {
    try {
      await axios('https://localhost:5000/register');

    }
    catch (error) {
      console.log("registration not completed")
    }

  }


  return (
    <div className='login-form'>
      <button type="button" class="close" data-dismiss="modal" aria-label="Close">
        <span aria-hidden="true"><i class="icon-autodeal-close"></i></span>
      </button>
      <div>
        <h4>Register</h4>
        <form action="" onSubmit={handleRegisterForm}>
          {/* Username */}
          <div className='input-box'>
            <label for="user-name" className='label'>User Name:</label>
            <div className='input-wrap'>
              <input
                type="text"
                value={username}
                placeholder='User name'
                id='user-name'
                required
                className='input'
                onChange={(e) => { setUsername(e.target.value) }}
              />
            </div>
          </div>
          {/* Email */}
          <div className='input-box'>
            <label for="user-name" className='label'>Email:</label>
            <div className='input-wrap'>
              <input
                type="text"
                value={email}
                placeholder='Your Email'
                id='user-name'
                required
                className='input'
                onChange={(e) => { setEmail(e.target.value) }}
              />
            </div>
          </div>
          {/* Password */}
          <div className='input-box'>
            <label for="user-name" className='label'>User Name:</label>
            <div className='input-wrap password-input'>
              <input
                type="password"
                value={password}
                placeholder='Your Password'
                id='user-name'
                required
                className='input'
                onKeyUp={(e) => { setPassword(e.target.value) }}
              />
              <button className='password-btn'>
                <i class="fa-regular fa-eye"></i>
                <i class="fa-regular fa-eye-slash"></i>
              </button>
            </div>
          </div>
          {/* Confirm password */}
          <div className='input-box'>
            <label for="user-name" className='label'>Confirm Password:</label>
            <div className='input-wrap'>
              <input
                type="text"
                // value={ }
                placeholder='User name'
                id='user-name'
                required
                className='input'
              />
            </div>
          </div>
          {/* Submit button */}
          <button className='btn'>Sign Up</button>
        </form>
      </div>
    </div>
  )
}

export default LoginForm
